import { useRef, useState } from 'react';
import React from 'react';
import './App.scss'; 
import { FaPlus, FaTrash, FaEdit, FaHornbill, FaExchangeAlt } from 'react-icons/fa';



import { Cart } from './components/cart/cart';

function App() {

  const [tab, setTab] = useState([
    ["QUITE", "CAŁKIEM"],
    ["ACTIVE", "AKTYWNY"],
    ["HANDED", "WRĘCZYĆ"],
    ["QUITE", "CAŁKIEM"],
    ["ACTIVE", "AKTYWNY"],
    ["HANDED", "WRĘCZYĆ"],
    ["QUITE", "CAŁKIEM"],
    ["ACTIVE", "AKTYWNY"],
    ["HANDED", "WRĘCZYĆ"]
  ]);

  const [referencePoint, setReferencePoint] = useState(0);
  const [rotate, setRotate] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [active, setActive] = useState(0); // która karta w danej chwili jest widoczna ! 
  const [animatedCart, setAnimatedCart] = useState( false );
  const [reverseCart, setReverseCart] = useState(false);
  const [newPl, setNewPl] = useState("");
  const [newEng, setNewEng] = useState("");

  const addCartDialog = useRef(null);


  const [state, saveState] = useState(tab.length); // zapisz stan, tak aby po zmianie danych
  // aplikacja wróciła do momentu na którym użytkownik skończył

  const setState = (index) => {
    saveState(index);
  }

  const handleMouseDown = (e) => {
    console.log("FIRST STATE");
    setIsDragging(true);
    setReferencePoint(e.clientX);
  } 

  const handleMouseMove = (e) => {
    if(isDragging){
      const mousePos_x = e.clientX;
      const vector_x = mousePos_x - referencePoint;
      setRotate(vector_x);
      console.log("Vector: " + vector_x);
    }
  }

  // const handleMouseUp = () => {

  //   if (rotate < -100){
  //     setActive(active+1);
  //   }

  //   if(rotate == 0){
  //     reverseCart();
  //   }


  //   setRotate(0);
  //   setIsDragging(false);
  // }

  const renderFlashcards = () => {


    const flaschcardsArray = [];

    for (let i=0; i<tab.length; i++){



      if (i <= state){
        flaschcardsArray.push(
            <Cart key={i} state={setState} reverse={reverseCart} zindex={i} eng ={tab[i][0]} pl = {tab[i][1]} method={deleteFlashcards} edit={editFlashcards}/>
        )
        }
    }

    return flaschcardsArray;
  }

  const deleteFlashcards = (index) => {
    saveState(index-1);
    const newTab = tab.filter((_, i) => i !== index);
    setTab(newTab);

  }

  const editFlashcards = (index, plFlash, engFlash) => {
    saveState(index);
    const newFlashcards = [...tab];
    newFlashcards[index][0] = engFlash.toUpperCase();
    newFlashcards[index][1] = plFlash.toUpperCase();
    setTab(newFlashcards);
  }

  // function reverseCart(){
  //   console.log("click");
  //   setAnimatedCart(!animatedCart);
  // }

  return (
    <div className="App">

      <div className='background-logo'>
        <FaHornbill size={100}/>
      </div>
      <div className="title">

        <div className='logo'>
          <FaHornbill />
        </div>

        <span>
          Flashcards
        </span>
      </div>

      <div className="cart-container-app">
        { renderFlashcards() }
      </div>

      <div className="menu">

        <button
          onClick={() => {setReverseCart(!reverseCart)}}  
        ><FaExchangeAlt size={25}/></button>
        <button
          onClick={() => {
            addCartDialog.current.showModal();
          }}
        ><FaPlus size={25}/></button>
      </div>

      <dialog className="edit-dialog" ref={addCartDialog}>
                DODAJ FISZKĘ:
                <div style={{
                    marginTop: '10px',
                    marginBottom: '10px',
                    width: '100%',
                    height: '2px',
                    background: 'black',
                }}>

                </div>
                <div style={{
                    with: '100%',
                    float: 'left',
                    fontSize: '20px',
                }}>POLSKA STRONA:</div>

                <input
                        style={{textTransform: 'uppercase',}}
                        className=""
                        type="text"
                        // placeholder="Nazwa lokalizacji"
                        value={newPl}
                        onChange={(e) => setNewPl(e.target.value)}
                />   

                <div style={{
                    marginTop: '5px',
                    with: '100%',
                    float: 'left',
                    fontSize: '20px',
                }}>ANGIELSKA STRONA:</div>

                <input
                    style={{textTransform: 'uppercase',}}
                    className=""
                    type="text"
                    // placeholder="Nazwa lokalizacji"
                    value={newEng}
                    onChange={(e) => setNewEng(e.target.value)}
                />

                <div style={{ justifyContent: 'space-between', 
                              display: 'flex',
                              marginTop: '10px' }}>
                    <button className="dialog-btn"
                        onClick={() => {


                          addCartDialog.current.close();
                        }
                        }   > 
                    ANULUJ</button>
                    <button className="dialog-btn"
                        onClick={() => {

                            const newFlashcardsok = [];

                            for (let i=0; i<tab.length; i++){
                              if (i == state){
                                newFlashcardsok.push([newPl, newEng]);
                              }
                              else {
                                newFlashcardsok.push([tab[i][0], tab[i][1]]);
                              }
                            }
                            setTab(newFlashcardsok);
                            saveState(state+1);


                            console.log(newFlashcardsok);
                            console.log("tabLength: " + tab.length);

                            setNewEng("");
                            setNewPl("");
                            addCartDialog.current.close();




                            ////////////////////////////
                            ////////////////////////////
                          //   const newFlashcard = [newEng, newPl];
                          //  const flashcards = tab.splice(state, 0, [newFlashcard]);

                          //   setTab(flashcards); // Dodaj nową fiszkę do tablicy
                          //   console.log(tab);
                          //   setNewEng(""); // Wyczyść pola
                          //   setNewPl("");
                          //   setState(state+1);
                          //   addCartDialog.current.close(); // Zamknij dialog

                        }}
                    >ZAPISZ</button>
                </div>
            </dialog>

    </div>
  );
}

export default App;
