import { useRef, useState } from 'react';
import './App.scss'; 
import { FaPlus, FaTrash, FaEdit } from 'react-icons/fa';





function App() {

  const [active, setActive] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const flashcardsWrapper = useRef(null);

  const tab = ["QUITE", "ACTIVE", "HANDED"];

  function renderFlashcards(){
    const fischcards =[];
    for(let i=0; i<3; i++){
      fischcards.push(
        <div className="cart" key={i} style={{
          '--active' : i === active ? 1 : 0,
          '--offset' : i,
        }}>
          {tab[i]}
        </div>
      )
    }

    return fischcards;
  }

  const handleMouseDown = (e) =>{
    const x = e.clientX;
    const y = e.clientY;
    setPosition({x, y});
    setIsDragging(true);
  }

  function handleMouseMove(){
    if(isDragging){
      const x = e.clientX;
      const y = e.clientY;
      const newPosition = [x-position.x, y-position.y];

      flashcardsWrapper.current.style.left = `${newPosition[0]}px`;
      flashcardsWrapper.current.style.top = `${newPosition[1]}px`;
    }
  }

  const handleMouseUp = () =>{
    setIsDragging(false);
  }

  return (
    <div className="App">
      <div className="reverse-button">
        <button>REVERSE</button>
      </div>

      <div className="cart-container"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        ref={flashcardsWrapper}>

        {renderFlashcards()}

        {/* <div className="cart">
          QUITE
        </div>

        <div className="cart">
          QUITE
        </div>

        <div className="cart">
          QUITE
        </div> */}

      </div>

      <div className="menu">
        <button><FaTrash size={25}/></button>
        <button><FaEdit size={25}/></button>
        <button><FaPlus size={25}/></button>

      </div>

    </div>
  );
}

export default App;
