import { useRef, useState } from 'react';
import './App.scss'; 
import { FaPlus, FaTrash, FaEdit } from 'react-icons/fa';


function App() {
  const tab = [
    ["QUITE", "CAŁKIEM"],
    ["ACTIVE", "AKTYWNY"],
    ["HANDED", "WRĘCZYĆ"]
  ];

  const [referencePoint, setReferencePoint] = useState(0);
  const [rotate, setRotate] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [active, setActive] = useState(0); // która karta w danej chwili jest widoczna ! 
  const [animatedCart, setAnimatedCart] = useState( false );

  const handleMouseDown = (e) => {
    console.log("FIRST STATE");
    setIsDragging(true);
    setReferencePoint(e.clientX);
  } 

  const handleMouseMove = (e) => {
    if(isDragging){
      const mousePos_x = e.clientX;
      const vector_x = mousePos_x - referencePoint;
      setRotate(vector_x);
      console.log("Vector: " + vector_x);
    }
  }

  const handleMouseUp = () => {

    if (rotate < -100){
      setActive(active+1);
    }

    if(rotate == 0){
      reverseCart();
    }


    setRotate(0);
    setIsDragging(false);
  }

  const renderFlashcards = () => {
    const flaschcardsArray = [];

    for (let i=0; i<tab.length; i++){
      flaschcardsArray.push(
        <div className={`cart ${animatedCart ? 'animated-cart' : ''}`} 

        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        style={{
        zIndex : i === active ? 10 : 0,
        // '--vector' : rotate < 0 ? 'left top' : 'right top',
        '--rotate' : i === active && rotate < 0? rotate/8 : 0,}}
        >{tab[i][0]}</div>
      )
    }

    return flaschcardsArray;
  }

  function reverseCart(){
    console.log("click");
    setAnimatedCart(!animatedCart);

    // if (word == pl){setWord(eng);} 
    // else if (word == eng){setWord(pl);} 
  }

  return (
    <div className="App">
      <div className="reverse-button">
        <button>REVERSE</button>
      </div>

      <div className="cart-container">

        { renderFlashcards() }


        {/* <div className='cart'
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        style={{
        zIndex : 4,
        '--rotate' : rotate/8,}}
        >QUIZ</div>

        <div className='cart'
        style={{zIndex : 3}}>HANDED</div> */}
      </div>

      <div className="menu">
        <button><FaTrash size={25}/></button>
        <button><FaEdit size={25}/></button>
        <button><FaPlus size={25}/></button>

      </div>

    </div>
  );
}

export default App;



