import { useRef, useState } from 'react';
import './App.scss'; 
import { FaPlus, FaTrash, FaEdit } from 'react-icons/fa';





function App() {

  const [opacity, setOpacity] = useState(0);
  const [active, setActive] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [currentPosition, setCurrentPosition] = useState(0);
  const flashcardsWrapper = useRef(null);

  const tab = ["QUITE", "ACTIVE", "HANDED"];

  function renderFlashcards(){
    const fischcards =[];
    for(let i=0; i<3; i++){
      fischcards.push(
        <div className="cart" key={i} style={{
          '--active' : i === active ? 1 : 0,
          '--offset' : i,
          '--opacity' : i === opacity ? 0 : 0,
        }}>
          {tab[i]}
        </div>
      )
    }

    return fischcards;
  }

  const handleMouseDown = (e) =>{
    const x = e.clientX;
    const y = e.clientY;
    setPosition({x, y});
    setIsDragging(true);
  }

  const handleMouseMove =(e) => {
    if(isDragging){
      const x = e.clientX;
      const y = e.clientY;
      const newPosition = [x-position.x, y-position.y];
      setCurrentPosition(newPosition[0]);

      if (newPosition[0] < 0){
        setOpacity(active+1);
      }

      if (newPosition[0] < -200){
        setActive(active + 1);
        setIsDragging(false);
      }

      console.log("Position: " + newPosition[0] + ", " + newPosition[1]);

      // flashcardsWrapper.current.style.left = `${newPosition[0]}px`;
      // flashcardsWrapper.current.style.top = `${newPosition[1]}px`;
    }
  }

  const handleMouseUp = () =>{
    setCurrentPosition(0);
    setIsDragging(false);
  }

  return (
    <div className="App">
      <div className="reverse-button">
        <button>REVERSE</button>
      </div>

      <div className="cart-container"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        ref={flashcardsWrapper}
        style={{
          '--offsetX' : currentPosition,
        }}>

        {renderFlashcards()}

        {/* <div className="cart">
          QUITE
        </div>

        <div className="cart">
          QUITE
        </div>

        <div className="cart">
          QUITE
        </div> */}

      </div>

      <div className="menu">
        <button><FaTrash size={25}/></button>
        <button><FaEdit size={25}/></button>
        <button><FaPlus size={25}/></button>

      </div>

    </div>
  );
}

export default App;
