import React from "react";
import { useState, useEffect } from "react";
// import {TiChevronLeftOutline, TiChevronRightOutline} from 'https://cdn.skypack.dev/react-icons/ti';
import "../cart/cart.css";


export function Cart( { eng = "QUITE", pl = "CAŁKIEM", 
                        perspective = "1200px", 
                        rotateY = 45,
                        scale = 0.6,
                        zindex = -1,
                        flashcards,
                        blur = "2px" } ){

    // VARIABLES
    const [word, setWord] = useState(pl);
    const [referencePoint, setReferencePoint] = useState(0);
    const [rotate, setRotate] = useState(0);
    const [isDragging, setIsDragging] = useState(false);
    const [active, setActive] = useState(0); // która karta w danej chwili jest widoczna ! 
    const [transformX, setTransformX] = useState(0); 
    const [transformY, setTransformY] = useState(0);


    // METHOD


    const handleMouseDown = (e) => {
        console.log("FIRST STATE");
        setIsDragging(true);
        setReferencePoint(e.clientX);
      } 
    
    const handleMouseMove = (e) => {
    if(isDragging){
        var mousePos_x = e.clientX;
        var vector_x = mousePos_x - referencePoint;
        setRotate(e.clientX - referencePoint);
        console.log("Vector: " + vector_x);
    }
    }

    const handleMouseUp = () => {

    if (rotate > -100){
        setRotate(0);

        // setActive(active+1);
    }

    else if (rotate < -100){
        setTransformX(-100);
        setRotate(0);
    }

    

    setIsDragging(false);
    }

    const handleTouchDown = (e) => {
        setWord("WORK");
        console.log("FIRST STATE");
        setIsDragging(true);
        setReferencePoint(e.clientX);
      } 
    
    const handleTouchMove = (e) => {
    if(isDragging){
        var mousePos_x = e.clientX;
        var vector_x = mousePos_x - referencePoint;
        setRotate(e.clientX - referencePoint);
        console.log("Vector: " + vector_x);
    }
    }

    const handleTouchUp = () => {

    if (rotate > -100){
        setRotate(0);

        // setActive(active+1);
    }

    else if (rotate < -100){
        setTransformX(-100);
        setRotate(0);
    }

    

    setIsDragging(false);
    }

    // useEffect(() => {
    //     reverseCart();
    // }, [eng]);

    // RETURN STATEMENT
    return(

        <div className="cart-container" 

            onClick={() => { 
                // reverse cart
                setWord(word === pl ? eng : pl);
            }}

            onTouchStart={(e) => {
                setIsDragging(true); 
                setReferencePoint(e.touches[0].clientX); 
            }}

            onTouchMove={(e) => {
                if(isDragging){
                    setRotate(e.touches[0].clientX - referencePoint);
                }
            }}

            onTouchEnd={(e) => {
            
                if(rotate > -100){
                    setRotate(0);
                }
                else if(rotate < -100){
                    setTransformX(-100);
                    setRotate(0);
                }

                setIsDragging(false);
            }}


            style={{
                '--test' : 'red',
                '--scale' : scale,
                '--rotate' : rotate/8,
                '--transformX' : transformX,
                '--zindex' : zindex
            }}
        >

        <span> {word} </span>
        </div>
    );
}
