import { useState, useEffect } from "react";
// import {TiChevronLeftOutline, TiChevronRightOutline} from 'https://cdn.skypack.dev/react-icons/ti';
import "../cart/cart.css";

export function Cart( { eng = "QUITE", pl = "CAŁKIEM", 
                        // reverseCart = false,
                        perspective = "1200px", 
                        rotateY = 45,
                        scale = 0.6,
                        // transformX = 0,
                        zindex = -1,
                        flashcards,
                        blur = "2px" } ){

    // VARIABLES
    const [animatedCart, setAnimatedCart] = useState( false );
    const [word, setWord] = useState(pl);
    const [referencePoint, setReferencePoint] = useState(0);
    const [rotate, setRotate] = useState(0);
    const [isDragging, setIsDragging] = useState(false);
    const [active, setActive] = useState(0); // która karta w danej chwili jest widoczna ! 
    const [transformX, setTransformX] = useState(0); 
    const [transformY, setTransformY] = useState(0);


    // METHOD
    function reverseCart(){
        setAnimatedCart(!animatedCart);

        if (word == pl){setWord(eng);} 
        else if (word == eng){setWord(pl);} 
    }

    const handleMouseDown = (e) => {
        console.log("FIRST STATE");
        setIsDragging(true);
        setReferencePoint(e.clientX);
      } 
    
    const handleMouseMove = (e) => {
    if(isDragging){
        var mousePos_x = e.clientX;
        var vector_x = mousePos_x - referencePoint;
        setRotate(e.clientX - referencePoint);
        console.log("Vector: " + vector_x);
    }
    }

    const handleMouseUp = () => {

    if (rotate > -100){
        setRotate(0);

        // setActive(active+1);
    }
     // else if(rotate == 0){
    //     // reverseCart();
    // }
    else if (rotate < -100){
        setTransformX(-100);
        setRotate(0);
    }

    

    setIsDragging(false);
    }

    useEffect(() => {
        reverseCart();
    }, [eng]);

    // RETURN STATEMENT
    return(
        <div 
            // className={`cart-container ${animatedCart ? 'animated-cart-container' : ''}`} 
            className="cart-container" 
            onClick={reverseCart}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            style={
                {'--test' : 'red',
                '--scale' : scale,
                '--rotate' : rotate/8,
                '--transformX' : transformX,
                '--transformY' : transformY,
                '--zindex' : zindex}
                }>

            <span> {word} </span>
        </div>
    );
}
