import { useRef, useState } from 'react';
import './App.scss'; 
import { FaPlus, FaTrash, FaEdit } from 'react-icons/fa';


function App() {
  const tab = ["QUITE", "ACTIVE", "HANDED"];

  const [referencePoint, setReferencePoint] = useState(0);
  const [isDragging, setIsDragging] = useState(false);

  const handleMouseDown = (e) => {
    console.log("FIRST STATE");
    setIsDragging(true);
    // setReferencePoint(e.clientX);
  } 

  const handleMouseMove = (e) => {
    if(isDragging){
      const mousePos_x = e.clientX;
      const vector_x = mousePos_x - referencePoint;
      console.log("Vector: " + vector_x);
    }
  }

  const handleMouseUp = () => {
    setIsDragging(false);
  }


  return (
    <div className="App">
      <div className="reverse-button">
        <button>REVERSE</button>
      </div>

      <div className="cart-container">
        <div className='cart'
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        >QUIZ</div>

        <div className='cart'>HANDED</div>
      </div>

      <div className="menu">
        <button><FaTrash size={25}/></button>
        <button><FaEdit size={25}/></button>
        <button><FaPlus size={25}/></button>

      </div>

    </div>
  );
}

export default App;



