import { useRef, useState } from 'react';
import './App.scss'; 
import { FaPlus, FaTrash, FaEdit } from 'react-icons/fa';


function App() {
  const tab = ["QUITE", "ACTIVE", "HANDED"];

  const [referencePoint, setReferencePoint] = useState(0);
  const [rotate, setRotate] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [active, setActive] = useState(2); // która karta w danej chwili jest widoczna ! 

  const handleMouseDown = (e) => {
    console.log("FIRST STATE");
    setIsDragging(true);
    setReferencePoint(e.clientX);
  } 

  const handleMouseMove = (e) => {
    if(isDragging){
      const mousePos_x = e.clientX;
      const vector_x = mousePos_x - referencePoint;
      setRotate(vector_x);
      console.log("Vector: " + vector_x);
    }
  }

  const handleMouseUp = () => {

    if (rotate > 100){

    }


    setRotate(0);
    setIsDragging(false);
  }

  const renderFlashcards = () => {
    const flaschcardsArray = [];

    for (let i=0; i<tab.length; i++){
      flaschcardsArray.push(
        <div className='cart'
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        style={{
        zIndex : i,
        '--rotate' : i === active ? rotate/8 : 0,}}
        >{tab[i]}</div>
      )
    }

    return flaschcardsArray;
  }

  return (
    <div className="App">
      <div className="reverse-button">
        <button>REVERSE</button>
      </div>

      <div className="cart-container">

        { renderFlashcards() }


        {/* <div className='cart'
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        style={{
        zIndex : 4,
        '--rotate' : rotate/8,}}
        >QUIZ</div>

        <div className='cart'
        style={{zIndex : 3}}>HANDED</div> */}
      </div>

      <div className="menu">
        <button><FaTrash size={25}/></button>
        <button><FaEdit size={25}/></button>
        <button><FaPlus size={25}/></button>

      </div>

    </div>
  );
}

export default App;



